import { Component, Input, OnChanges, SimpleChanges } from '@angular/core';

@Component({
  selector: 'app-game',
  templateUrl: './game.component.html',
  styleUrls: ['./game.component.css']
})
export class GameComponent {

  @Input() username: string = '';

  constructor(private http: HttpClient) { }

  ngOnChanges(changes: SimpleChanges) {
    if (changes.username) {
      console.log('Username changed:', this.username);
    }
  }

  continueGame() {
    this.http.post('YOUR_API_ENDPOINT/continue-game', { username: this.username })
      .subscribe(response => {
        console.log('Continue game response:', response);
      });
  }

  startGame() {
    const body = { username: this.username, action: 'start' };
    this.http.post('YOUR_API_ENDPOINT/start-game', body)
      .subscribe(response => {
        console.log('Start game response:', response);
      });
  }

  joinGame() {
    const body = { username: this.username, action: 'join' };
    this.http.post('YOUR_API_ENDPOINT/join-game', body)
      .subscribe(response => {
        console.log('Join game response:', response);
      });
  }
}
