<div>
    <div [ngSwitch]="componentShown">
        <div *ngSwitchCase="'meni'" class="game-actions-container">
            <button mat-raised-button color="primary" (click)="continueGame()">Continue Game</button>
            <button mat-raised-button color="accent" (click)="startGame()">Start Game</button>
            <button mat-raised-button color="warn" (click)="joinGame()">Join Game</button>
        </div>
        <div *ngSwitchCase="'start'" class="game-actions-container">
            <h2>Start a NEW game:</h2>
            <form (ngSubmit)="startGameRequest()">
                <mat-list>
                    <mat-list-item>
                        <mat-form-field appearance="fill">
                            <mat-label>Number of bots (1-3)</mat-label>
                            <input matInput type="number" [(ngModel)]="botNo" name="botNo" required>
                        </mat-form-field>
                    </mat-list-item>
                    <mat-list-item>
                        <button mat-raised-button color="primary" type="submit">Start</button>
                    </mat-list-item>
                    <mat-list-item>
                        <button mat-raised-button color="primary" (click)="meni()">Menu</button>
                    </mat-list-item>
                </mat-list>
            </form>
        </div>
        <div *ngSwitchCase="'join'" class="game-actions-container">
            <h2>Start an existing game:</h2>
            <form (ngSubmit)="joinGameRequest()">
                <mat-list>
                    <mat-list-item>
                        <mat-form-field appearance="fill">
                            <mat-label>Game ID (ask a fried)</mat-label>
                            <input matInput type="number" [(ngModel)]="gameId" name="gameId" required>
                        </mat-form-field>
                    </mat-list-item>
                    <mat-list-item>
                        <button mat-raised-button color="primary" type="submit">Join</button>
                    </mat-list-item>
                    <mat-list-item>
                        <button mat-raised-button color="primary" (click)="meni()">Menu</button>
                    </mat-list-item>
                </mat-list>
            </form>
        </div>
        <div *ngSwitchCase="'ingame'">
            <h2>Start an existing game:</h2>
        </div>
    </div>
</div>
