import { Component } from '@angular/core';

@Component({
  selector: 'app-game',
  templateUrl: './game.component.html',
  styleUrls: ['./game.component.css']
})
export class GameComponent {

}
<div class="game-actions-container">
  <button mat-raised-button color="primary" (click)="continueGame()">Continue Game</button>
  <button mat-raised-button color="accent" (click)="startGame()">Start Game</button>
  <button mat-raised-button color="warn" (click)="joinGame()">Join Game</button>
</div>
